mapper.select:
SELECT  history_no,
        upper_agency_id,
        upper_agency_name,
        agency_type,
        agency_id,
        agency_name,
        member_id,
        member_name,
        virtual_id,
        virtual_name,
        send_type,
        history_type,
        approval_status,
        amount,
        wire_transfer_fee,
        memo,
        tid,
        created_at
FROM TB_VIRTUAL_WALLET_HISTORY
WHERE TRUE

    AND created_at BETWEEN #{startDate} AND #{endDate}


    AND upper_agency_id = #{upperAgencyId}


    AND agency_id = #{agencyId}


    AND agency_type = #{agencyType}


    AND virtual_id = #{virtualId}


    AND send_type = #{sendType}


    AND history_type = #{historyType}


    AND approval_status = #{approvalStatus}



        AND agency_id = #{keyword}


        AND agency_name = #{keyword}


        AND member_id = #{memberId}


        AND member_name = #{memberName}


ORDER BY history_no DESC
LIMIT #{rows} OFFSET #{offset}
